import {useParams} from 'react-router-dom';
import { useState, useEffect } from 'react';


function Products () {
    const params = useParams();
    const productId = params.productid;

    const [currentProduct, setCurrentProduct] = useState([]);

    useEffect(() => {
        fetch('http://localhost:3000/products/'+productId)
            .then(response => response.json())
            .then(data => {
                console.log(data);
                setCurrentProduct(data);
            })
            .catch((err) => {
                console.log(err.message);
            });
    }, [setCurrentProduct]);

    return(
        <div class="pageContentWrapper">
            <div class="productGallery">
            <img src="./images/mural01.jpg" />
            </div>
            <div class="productTextWrapper">
                <h2 class="productName">Tattoes</h2>
                <h2 class="productPrice">60$</h2>
                <div class="productShipping">Shipping costs will be calculated at checkout.</div>
                <div class="productOrderFormWrapper">
                    <form class="productOrderForm"></form>
                </div>
                <section class="product-description">
                    <p>Flash Sheet #1 by Creature Creature</p>
                    <p>A4 size, Fine art and archival printed on Canson Printmaking Rag Paper</p>
                    <p>Free click and collect from our studio space in Collingwood, Victoria - get in touch with us for payment contact@creaturecreature.com.au</p>
                    <p>Includes 10% GST within Australia.</p>
                    <p>Prices:<br/>
        $60 + $5 postage within Australia - Approx 3 days (allow up to 5 business days)</p>
                    <p>**Please note due to Covid-19 we are incurring large delays and higher costs with postage outside of Australia.**</p>
                    <p>$60 + $20 postage international - Approx 8 business days depending on Country.</p>
                </section>

            </div>
        </div>
    )
}
export default Products;
