function MenuItem(props) {
    console.log(window.location.pathname);
    console.log(props.linktopage);

  const isSelected = window.location.pathname === props.linktopage;
  return (
    <li class={isSelected}>
        <a href={props.linktopage}>
            {props.text}
        </a>
    </li>
  );
}

export default MenuItem;
