import {useParams} from 'react-router-dom';
import { useState, useEffect } from 'react';


function Products () {
    const params = useParams();
    const productId = params.productid;

    const [currentProduct, setCurrentProduct] = useState([]);

    useEffect(() => {
        fetch('http://localhost:3000/products/'+productId)
            .then(response => response.json())
            .then(data => {
                console.log(data);
                setCurrentProduct(data);
            })
            .catch((err) => {
                console.log(err.message);
            });
    }, [setCurrentProduct]);

    if (typeof currentProduct.product === 'object' && currentProduct.product !== null){
        const product = currentProduct.product;
        const productImage = product.image;
        const productTitle = product.title;
        const productPrice = product.price;
        const productDescription = product.description;
        return(
            <div class="pageContentWrapper">
                <div class="productGallery">
                <img src={productImage} />
                </div>
                <div class="productTextWrapper">
                    <h2 class="productName">{productTitle}</h2>
                    <h2 class="productPrice">{productPrice}</h2>
                    <div class="productShipping">Shipping costs will be calculated at checkout.</div>
                    <div class="productOrderFormWrapper">
                        <form class="productOrderForm"></form>
                    </div>
                    <section class="product-description" dangerouslySetInnerHTML={{ __html: productDescription }}>
                        
                        {productDescription}
                    </section>
    
                </div>
            </div>
        )
    }
    else
    {
        return (
            <div>
                <p>Product not found.</p>
            </div>
        );
    }

    
}
export default Products;
